package basic06_io;

import java.io.File;
import java.io.RandomAccessFile;

public class RandomAccessFileTest {

	public RandomAccessFileTest() {
		// TODO Auto-generated constructor stub
	}

	void start() {
		try {
			// RandomAccessFile : 비 순차적 입출력
			// new RandowAccessFile(file, mode); mode : r-> 읽기전용, w->쓰기전용, rw->읽기쓰기모두 허용
			File f = new File("C://Users/Jiho Jung/Desktop/dev/student/Random.txt");
			RandomAccessFile raf = new RandomAccessFile(f, "rw");
			//            012345678901234567890
			String str = "JAVA Random Access File Test...";
			raf.writeChars(str);
			
			int dataInt = 1234;
			raf.writeInt(dataInt);
			
			double dataDouble = 89.36;
			raf.writeDouble(dataDouble);
			
			//////////////////////////
			String txt = "자바프로그래밍";
			// 위치를 쓸 곳으로 이동
			raf.seek(5);
			raf.writeChars(txt);
			
			
			// 읽기
			raf.seek(20);
			byte[] data = new byte[10];
			int cnt = raf.read(data, 0, data.length);
			System.out.println(new String(data));
			
			raf.close();
		} catch (Exception e) {
			e.printStackTrace();
		}
	}
	
	public static void main(String[] args) {
		
		new RandomAccessFileTest().start();

	}

}
